import React, { useState } from 'react';
const OrderPanel = () => {
  const [form, setForm] = useState({ type: 'Buy', amount: '', price: '' });
  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSubmit = () => alert(JSON.stringify(form, null, 2));
  return (
    <div className="bg-white dark:bg-gray-800 p-4 rounded shadow-md">
      <h3 className="font-semibold mb-2 dark:text-white">Order Panel</h3>
      <select name="type" value={form.type} onChange={handleChange} className="w-full p-2 mb-2 border rounded">
        <option>Buy</option><option>Sell</option>
      </select>
      <input name="amount" type="number" placeholder="Amount" value={form.amount} onChange={handleChange} className="w-full p-2 mb-2 border rounded"/>
      <input name="price" type="number" placeholder="Price" value={form.price} onChange={handleChange} className="w-full p-2 mb-2 border rounded"/>
      <button onClick={handleSubmit} className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700">Submit</button>
    </div>);
};
export default OrderPanel;
