// src/components/portfolio/WalletCard.js
import React, { useState } from 'react';
import { Wallet, Plus } from 'lucide-react';
import Card from '../common/Card';
import Button from '../common/Button';
import { formatCurrency } from '../../utils/formatters';

const WalletCard = ({ balance, onRecharge, isLoading }) => {
  const [rechargeAmount, setRechargeAmount] = useState('');
  const [showRecharge, setShowRecharge] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleRecharge = async () => {
    if (rechargeAmount && parseFloat(rechargeAmount) > 0) {
      try {
        setLoading(true);
        await onRecharge(parseFloat(rechargeAmount));
        setRechargeAmount('');
        setShowRecharge(false);
      } catch (error) {
        console.error('Recharge failed:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="bg-gradient-to-br from-blue-500 to-purple-600 p-6 rounded-xl shadow-lg text-white">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center space-x-2">
          <Wallet size={24} />
          <h3 className="text-lg font-semibold">Wallet Balance</h3>
        </div>
        <button
          onClick={() => setShowRecharge(!showRecharge)}
          className="bg-white/20 hover:bg-white/30 p-2 rounded-lg transition-colors"
          disabled={loading}
        >
          <Plus size={16} />
        </button>
      </div>
      
      <div className="text-3xl font-bold mb-4">
        {isLoading ? (
          <div className="w-32 h-8 bg-white/20 rounded animate-pulse"></div>
        ) : (
          formatCurrency(balance)
        )}
      </div>
      
      {showRecharge && (
        <div className="space-y-2">
          <input
            type="number"
            placeholder="Amount to recharge"
            value={rechargeAmount}
            onChange={(e) => setRechargeAmount(e.target.value)}
            className="w-full p-2 rounded bg-white/20 placeholder-white/70 text-white border-none focus:ring-2 focus:ring-white/50"
            disabled={loading}
          />
          <Button
            onClick={handleRecharge}
            loading={loading}
            className="w-full bg-white/20 hover:bg-white/30"
            disabled={!rechargeAmount || parseFloat(rechargeAmount) <= 0}
          >
            Recharge Wallet
          </Button>
        </div>
      )}
    </div>
  );
};

export default WalletCard;
