import React from 'react';
const PortfolioOverview = ({ holdings, totalInvested, currentValue }) => {
  const pnlPercent = (((currentValue - totalInvested)/totalInvested)*100).toFixed(2);
  return (
    <div className="bg-white dark:bg-gray-800 p-4 rounded shadow-md">
      <h3 className="font-semibold mb-2 dark:text-white">Portfolio Overview</h3>
      <div>Total Invested: ₹{totalInvested}</div>
      <div>Current Value: ₹{currentValue}</div>
      <div>P&L: {pnlPercent}%</div>
    </div>);
};
export default PortfolioOverview;
