import React, { useEffect, useState } from 'react';
const PriceMarquee = ({ symbols }) => {
  const [prices, setPrices] = useState([]);
  useEffect(() => {
    const fetch = () => {
      setPrices(symbols.map(sym => ({
        symbol: sym,
        price: (Math.random()*1000).toFixed(2),
        change: ((Math.random()-0.5)*2).toFixed(2)
      })));
    };
    fetch();
    const id = setInterval(fetch,5000);
    return () => clearInterval(id);
  }, [symbols]);
  return (
    <div className="bg-gray-800 text-white py-2 overflow-x-auto whitespace-nowrap">
      {prices.map(p => (
        <span key={p.symbol} className={`mx-4 ${p.change>=0?'text-green-400':'text-red-400'}`}>
          {p.symbol}: â‚¹{p.price} ({p.change}%)
        </span>
      ))}
    </div>);
};
export default PriceMarquee;
