import React, { useState } from 'react';
import PriceMarquee from './components/PriceMarquee';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Chart from './components/Chart';
import PortfolioOverview from './components/PortfolioOverview';
import OrderPanel from './components/OrderPanel';

const sampleChart = {
  labels: ['9:30','10:00','10:30','11:00','11:30'],
  datasets: [{ label: 'NIFTY', data: [20000,20100,19950,20200,20350], borderColor: '#3b82f6', fill: false }],
};
const sampleHoldings = [{ symbol: 'RELIANCE', qty: 10 },{ symbol: 'TCS', qty: 5 }];

function App() {
  const [darkMode, setDarkMode] = useState(false);
  return (
    <div className={darkMode ? 'dark' : ''}>
      <div className="h-screen flex flex-col dark:bg-gray-900 dark:text-white">
        <PriceMarquee symbols={['RELIANCE','TCS','INFY','HDFC']} />
        <div className="flex h-full">
          <Sidebar />
          <div className="flex flex-col flex-1 p-4 gap-4 overflow-auto">
            <Header toggleDarkMode={() => setDarkMode(!darkMode)} darkMode={darkMode} />
            <div className="grid grid-cols-2 gap-4 flex-1">
              <Chart data={sampleChart} />
              <PortfolioOverview holdings={sampleHoldings} totalInvested={100000} currentValue={110000} />
              <OrderPanel />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
